import React, { useState } from 'react';
import Condition from '../Conditions/Conditions';
import classes from './Forecast.module.css'
const Forecast = () => {
  const [city, setCity] = useState('');
  const [unit, setUnit] = useState('imperial');
  const [responseObj, setResponseObj] = useState({});
  const[error, setError] = useState(false);
  const[loading, setLoading] = useState(false)

  const uriEncodedCity = encodeURIComponent(city);

  function getForecast(e) {
    e.preventDefault()
    if(city.length === 0 ) {
      return setError(true)
    }

 

    //Clear state in preparartion for new data
    setError(false)
    setResponseObj({})
    setLoading(true)

    

    fetch(`https://community-open-weather-map.p.rapidapi.com/weather?units=${unit}&q=${uriEncodedCity}`, {
	"method": "GET",
	"headers": {
		"x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
		"x-rapidapi-key": "3d81810033msh4083baf2f7e8eb3p19d736jsn9bb433da78ba"

	}
  
})
.then(response => response.json())
  .then(response => {
    if(response.cod !== 200) {
      throw new Error()
    }
    setResponseObj(response)
    setLoading(false)
  })
  .catch(err => {
    setError(true);
    setLoading(false);
    console.log(err.message)
  })
  }

  return (
    <div>
      <h2>
        Find Current Weather Conditons
      </h2>
      <div>
        <Condition
          responseObj={responseObj}
          error={error}
          
        />
      </div>
      <form on onSubmit={getForecast}>
        <input  
          type='text'
          placeholder='Enter City'
          maxLength='50'
          value={city}
          className={classes.textInput}
          onChange={(e) => setCity(e.target.value)}
        />
        <label className={classes.Radio}>
          <input
            type='radio'
            name='units'
            checked={unit === 'imperial'}
            value='imperial'
            onChange={(e) => setUnit (e.target.value)}
          />
          Fahrenheit
        </label>
        <label className={classes.Radio}>
          <input
            type='radio'
            name='units'
            checked={unit === 'metric'}
            value='metric'
            onChange={(e) => setUnit(e.target.value)}
            />
            Celcius
        </label>
        <button className={classes.Button} type='submit'>Get Forecast</button>
      </form>

     
    </div>

  )
} 

export default Forecast;
